mapper.insert:
insert into group_member(uuid, groupId, folder, role, level, groupRemark, nickName, noticeType, insertTime, lastTime)
values(#{uuid}, #{groupId}, #{folder}, #{role}, #{level}, #{groupRemark}, #{nickName}, #{noticeType}, #{insertTime}, #{lastTime});
update group_info set memberCount=memberCount+1 where groupId=#{groupId};
